const Example = () => {
  const [isChecked, setIsChecked] = useState(true);
  return (
    <>
      <div>
        <label htmlFor="my-check">チェック :</label>
        <input
          type="checkbox"
          id="my-check"
          checked={isChecked}
          onChange={(e) => setIsChecked((prevState) => !prevState)}
        />
        <div>{isChecked ? "ON!" : "OFF!"}</div>
      </div>
    </>
  );
};

export default Example;
